$(function(){var e=$(".box"),n=$(e).length;$("#counter").find("span").text(n);var c=$(e).find("input[type='checkbox']");$(c).prop("checked",!1),$(c).on("change",function(){$(this).parent("label").siblings("label").children("input").prop("checked",!1);var c=$(e).find("input[type='checkbox']:checked").size();$("#counter").find("span").text(n-c),$(this).parent("label").toggleClass("checked").siblings("label").removeClass("checked")})});